import React, { useEffect, useState } from "react";
import SectionCard from "../SectionCard";
import {
  fetchAllPlansAdmin,
  createPlanAdmin,
  updatePlanAdmin,
  deletePlanAdmin,
} from "../../controllers/subscriptionController";

export default function SuperUsuarioPlanes() {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  // crear
  const [createForm, setCreateForm] = useState({
    nombre: "",
    precio: "",
    estado: "activo",
  });

  // editar
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({
    nombre: "",
    precio: "",
    estado: "activo",
  });

  const load = async () => {
    setLoading(true);
    setMsg("");
    try {
      const { response, data } = await fetchAllPlansAdmin();
      if (!response.ok) {
        setMsg(data.error || "No se pudo cargar planes.");
        setPlans([]);
        return;
      }
      setPlans(data.plans || []);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const onCreate = async (e) => {
    e.preventDefault();
    setMsg("");

    const payload = {
      nombre: (createForm.nombre || "").trim(),
      precio: createForm.precio,
      estado: createForm.estado || "activo",
    };

    const { response, data } = await createPlanAdmin(payload);
    if (!response.ok) {
      setMsg(data.error || "No se pudo crear el plan.");
      return;
    }

    setCreateForm({ nombre: "", precio: "", estado: "activo" });
    setMsg(data.message || "Plan creado.");
    await load();
  };

  const startEdit = (p) => {
    setEditingId(p.id_plan);
    setEditForm({
      nombre: p.nombre || "",
      precio: String(p.precio ?? ""),
      estado: p.estado || "activo",
    });
    setMsg("");
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditForm({ nombre: "", precio: "", estado: "activo" });
  };

  const saveEdit = async () => {
    if (!editingId) return;
    setMsg("");

    const payload = {
      nombre: (editForm.nombre || "").trim(),
      precio: editForm.precio,
      estado: editForm.estado,
    };

    const { response, data } = await updatePlanAdmin(editingId, payload);
    if (!response.ok) {
      setMsg(data.error || "No se pudo actualizar.");
      return;
    }

    setMsg(data.message || "Actualizado.");
    cancelEdit();
    await load();
  };

  const deactivateLogical = async (planId) => {
    setMsg("");
    const { response, data } = await deletePlanAdmin(planId);
    if (!response.ok) {
      setMsg(data.error || "No se pudo desactivar.");
      return;
    }
    setMsg(data.message || "Plan desactivado.");
    await load();
  };

  const activate = async (planId) => {
    setMsg("");
    const { response, data } = await updatePlanAdmin(planId, { estado: "activo" });
    if (!response.ok) {
      setMsg(data.error || "No se pudo activar.");
      return;
    }
    setMsg(data.message || "Plan activado.");
    await load();
  };

  return (
    <SectionCard
      title="Planes"
      description="Crea, edita y desactiva (eliminación lógica) planes del sistema."
    >
      <div className="card">
        <div style={{ display: "flex", gap: 10, marginBottom: 12 }}>
          <button type="button" className="ghost-button" onClick={load} disabled={loading}>
            {loading ? "Cargando..." : "Recargar"}
          </button>
        </div>

        {msg && (
          <p className={msg.toLowerCase().includes("no") || msg.toLowerCase().includes("error") ? "error" : "muted"}>
            {msg}
          </p>
        )}

        {/* Crear */}
        <form onSubmit={onCreate} style={{ marginBottom: 14 }}>
          <div className="data-row" style={{ alignItems: "end" }}>
            <div style={{ flex: 1 }}>
              <div className="muted" style={{ marginBottom: 6 }}>Nombre</div>
              <input
                value={createForm.nombre}
                onChange={(e) => setCreateForm((p) => ({ ...p, nombre: e.target.value }))}
                required
              />
            </div>

            <div style={{ width: 160 }}>
              <div className="muted" style={{ marginBottom: 6 }}>Precio</div>
              <input
                value={createForm.precio}
                onChange={(e) => setCreateForm((p) => ({ ...p, precio: e.target.value }))}
                placeholder="Ej: 50"
                required
              />
            </div>

            <div style={{ width: 160 }}>
              <div className="muted" style={{ marginBottom: 6 }}>Estado</div>
              <select
                value={createForm.estado}
                onChange={(e) => setCreateForm((p) => ({ ...p, estado: e.target.value }))}
              >
                <option value="activo">activo</option>
                <option value="inactivo">inactivo</option>
              </select>
            </div>

            <div style={{ width: 160 }}>
              <button type="submit" className="link-button">
                Crear plan
              </button>
            </div>
          </div>
        </form>

        {/* Lista */}
        {plans.length === 0 ? (
          <p className="muted">No hay planes.</p>
        ) : (
          <div className="data-list">
            {plans.map((p) => {
              const isEditing = editingId === p.id_plan;
              const isActive = (p.estado || "").toLowerCase() === "activo";

              return (
                <div className="data-row" key={p.id_plan}>
                  <div style={{ flex: 1 }}>
                    {!isEditing ? (
                      <>
                        <div style={{ fontWeight: 600 }}>{p.nombre}</div>
                        <div className="muted">Precio: {p.precio}</div>
                        <div className="muted">Estado: {p.estado}</div>
                      </>
                    ) : (
                      <div style={{ display: "grid", gap: 8 }}>
                        <div>
                          <div className="muted" style={{ marginBottom: 6 }}>Nombre</div>
                          <input
                            value={editForm.nombre}
                            onChange={(e) => setEditForm((s) => ({ ...s, nombre: e.target.value }))}
                          />
                        </div>

                        <div style={{ display: "flex", gap: 10 }}>
                          <div style={{ width: 160 }}>
                            <div className="muted" style={{ marginBottom: 6 }}>Precio</div>
                            <input
                              value={editForm.precio}
                              onChange={(e) => setEditForm((s) => ({ ...s, precio: e.target.value }))}
                            />
                          </div>

                          <div style={{ width: 160 }}>
                            <div className="muted" style={{ marginBottom: 6 }}>Estado</div>
                            <select
                              value={editForm.estado}
                              onChange={(e) => setEditForm((s) => ({ ...s, estado: e.target.value }))}
                            >
                              <option value="activo">activo</option>
                              <option value="inactivo">inactivo</option>
                            </select>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>

                  <div className="row-actions" style={{ gap: 8 }}>
                    {!isEditing ? (
                      <>
                        <button type="button" className="ghost-button" onClick={() => startEdit(p)}>
                          Editar
                        </button>

                        {isActive ? (
                          <button
                            type="button"
                            className="danger-button"
                            onClick={() => deactivateLogical(p.id_plan)}
                          >
                            Desactivar
                          </button>
                        ) : (
                          <button
                            type="button"
                            className="ghost-button"
                            onClick={() => activate(p.id_plan)}
                          >
                            Activar
                          </button>
                        )}
                      </>
                    ) : (
                      <>
                        <button type="button" className="link-button" onClick={saveEdit}>
                          Guardar
                        </button>
                        <button type="button" className="ghost-button" onClick={cancelEdit}>
                          Cancelar
                        </button>
                      </>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </SectionCard>
  );
}
