import React, { useEffect, useState } from "react";
import {
  BrowserRouter,
  Routes,
  Route,
  NavLink,
  Navigate,
  useLocation,
} from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE || "";

const emptyForm = {
  username: "",
  email: "",
  password: "",
};

const pageTitles = {
  "/dashboard": "Dashboard",
  "/categorias": "Categorías",
  "/planes": "Planes",
  "/usuarios": "Usuarios",
  "/perfil": "Perfil",
};

const SectionCard = ({ title, description, children }) => (
  <section className="content-card">
    <h2>{title}</h2>
    {description && <p>{description}</p>}
    {children}
  </section>
);

const DashboardHeader = ({ user, initials, menuOpen, setMenuOpen, onLogout }) => {
  const location = useLocation();
  const title = pageTitles[location.pathname] || "Dashboard";

  return (
    <header className="topbar">
      <h1>{title}</h1>
      <div className="user-menu">
        <button
          type="button"
          className="avatar-button"
          onClick={() => setMenuOpen((prev) => !prev)}
        >
          <span className="avatar">{initials}</span>
          <span className="avatar-name">{user.username}</span>
        </button>
        {menuOpen && (
          <div className="dropdown">
            <NavLink
              to="/perfil"
              className="dropdown-link"
              onClick={() => setMenuOpen(false)}
            >
              Perfil
            </NavLink>
            <button type="button" onClick={onLogout}>
              Cerrar sesión
            </button>
          </div>
        )}
      </div>
    </header>
  );
};

export default function App() {
  const [form, setForm] = useState(emptyForm);
  const [user, setUser] = useState(null);
  const [mode, setMode] = useState("login");
  const [message, setMessage] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);

  const loadMe = async () => {
    const response = await fetch(`${API_BASE}/api/me`, {
      credentials: "include",
    });
    const data = await response.json();
    setUser(data.user);
  };

  useEffect(() => {
    loadMe();
  }, []);

  const handleChange = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setMessage("");

    const endpoint = mode === "login" ? "/api/login" : "/api/register";
    const response = await fetch(`${API_BASE}${endpoint}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify(form),
    });

    const data = await response.json();
    if (!response.ok) {
      setMessage(data.error || "Ocurrió un error");
      return;
    }

    setUser(data.user);
    setForm(emptyForm);
    setMenuOpen(false);
  };

  const handleLogout = async () => {
    await fetch(`${API_BASE}/api/logout`, {
      method: "POST",
      credentials: "include",
    });
    setUser(null);
    setMenuOpen(false);
  };

  const initials = user?.username
    ? user.username.slice(0, 2).toUpperCase()
    : "US";

  return (
    <BrowserRouter>
      {user ? (
        <div className="app-shell">
          <aside className="sidebar">
            <div className="brand">Microempresa</div>
            <nav className="menu">
              <NavLink
                to="/categorias"
                className={({ isActive }) =>
                  `menu-item${isActive ? " active" : ""}`
                }
              >
                Categorías
              </NavLink>
              <NavLink
                to="/planes"
                className={({ isActive }) =>
                  `menu-item${isActive ? " active" : ""}`
                }
              >
                Planes
              </NavLink>
              <NavLink
                to="/usuarios"
                className={({ isActive }) =>
                  `menu-item${isActive ? " active" : ""}`
                }
              >
                Usuarios
              </NavLink>
            </nav>
          </aside>
          <div className="main">
            <DashboardHeader
              user={user}
              initials={initials}
              menuOpen={menuOpen}
              setMenuOpen={setMenuOpen}
              onLogout={handleLogout}
            />
            <Routes>
              <Route
                path="/"
                element={<Navigate to="/dashboard" replace />}
              />
              <Route
                path="/dashboard"
                element={
                  <SectionCard
                    title={`Bienvenido, ${user.username}`}
                    description="Selecciona una opción del menú para continuar."
                  />
                }
              />
              <Route
                path="/categorias"
                element={
                  <SectionCard
                    title="Categorías"
                    description="Administra las categorías del sistema."
                  />
                }
              />
              <Route
                path="/planes"
                element={
                  <SectionCard
                    title="Planes"
                    description="Gestiona los planes disponibles."
                  />
                }
              />
              <Route
                path="/usuarios"
                element={
                  <SectionCard
                    title="Usuarios"
                    description="Administra los usuarios registrados."
                  />
                }
              />
              <Route
                path="/perfil"
                element={
                  <SectionCard title="Perfil">
                    <p>
                      Usuario: {user.username}
                      {user.email ? ` (${user.email})` : ""}
                    </p>
                  </SectionCard>
                }
              />
              <Route
                path="*"
                element={<Navigate to="/dashboard" replace />}
              />
            </Routes>
          </div>
        </div>
      ) : (
        <div className="container">
          <h1>Login y autenticación</h1>

          <form className="card" onSubmit={handleSubmit}>
            <div className="tabs">
              <button
                type="button"
                className={mode === "login" ? "active" : ""}
                onClick={() => setMode("login")}
              >
                Login
              </button>
              <button
                type="button"
                className={mode === "register" ? "active" : ""}
                onClick={() => setMode("register")}
              >
                Registro
              </button>
            </div>

            <label>
              Usuario
              <input
                name="username"
                value={form.username}
                onChange={handleChange}
                required
              />
            </label>

            {mode === "register" && (
              <label>
                Email
                <input
                  name="email"
                  type="email"
                  value={form.email}
                  onChange={handleChange}
                />
              </label>
            )}

            <label>
              Password
              <input
                name="password"
                type="password"
                value={form.password}
                onChange={handleChange}
                required
              />
            </label>

            <button type="submit">
              {mode === "login" ? "Entrar" : "Registrar"}
            </button>

            {message && <p className="error">{message}</p>}
          </form>
        </div>
      )}
    </BrowserRouter>
  );
}
