import React, { useEffect, useMemo, useState } from "react";
import { Link, Navigate, Route, Routes, useNavigate } from "react-router-dom";
import { QRCodeCanvas } from "qrcode.react";

import SectionCard from "../SectionCard";
import {
  fetchPlans,
  getOnboardingStatus,
  startMicroempresaOnboarding,
  submitMicroempresaPayment,
} from "../../controllers/subscriptionController";

const storage = {
  get: (k, fallback = "") => localStorage.getItem(k) || fallback,
  set: (k, v) => localStorage.setItem(k, String(v ?? "")),
  del: (k) => localStorage.removeItem(k),
};

const KEY_SIGNUP_ID = "onb_signup_id";
const KEY_TENANT_ID = "onb_tenant_id";
const KEY_EMAIL = "onb_email";
const KEY_PLAN_ID = "onb_plan_id";
const KEY_PLAN_NAME = "onb_plan_name";
const KEY_PLAN_PRICE = "onb_plan_price";

const initialForm = {
  nombre: "",
  logo_url: "",
  direccion: "",
  horario_inicio: "",
  horario_fin: "",
  nombre_propietario: "",
  apellido_paterno_propietario: "",
  apellido_materno_propietario: "",
  email: "",
  password: "",
};

export default function MicroempresaSignupWizard() {
  const navigate = useNavigate();

  // form paso 1
  const [form, setForm] = useState(() => ({
    ...initialForm,
    email: storage.get(KEY_EMAIL, ""),
  }));

  // planes paso 2
  const [plans, setPlans] = useState([]);
  const [plansLoading, setPlansLoading] = useState(false);

  // estado general
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  // seleccion plan
  const selectedPlanId = storage.get(KEY_PLAN_ID, "");
  const selectedPlanName = storage.get(KEY_PLAN_NAME, "");
  const selectedPlanPrice = storage.get(KEY_PLAN_PRICE, "");

  const signupId = storage.get(KEY_SIGNUP_ID, "");
  const tenantId = storage.get(KEY_TENANT_ID, "");

  const onChange = (e) => setForm((p) => ({ ...p, [e.target.name]: e.target.value }));

  const validateSchedule = () => {
    if (!form.horario_inicio || !form.horario_fin) return false;
    return form.horario_inicio < form.horario_fin;
  };

  // QR payload
  const qrValue = useMemo(() => {
    const payload = {
      app: "microempresa-saas",
      tenant_id: tenantId || null,
      signup_id: signupId || null,
      plan_id: selectedPlanId || null,
      plan: selectedPlanName || null,
      amount: selectedPlanPrice || null,
      email: storage.get(KEY_EMAIL, form.email).trim() || null,
      ts: new Date().toISOString(),
    };
    return JSON.stringify(payload);
  }, [tenantId, signupId, selectedPlanId, selectedPlanName, selectedPlanPrice, form.email]);

  const loadPlans = async () => {
    setPlansLoading(true);
    setMessage("");
    try {
      const { response, data } = await fetchPlans();
      if (!response.ok) {
        setMessage(data.error || "No se pudo cargar planes.");
        return;
      }
      setPlans(data.plans || []);
    } finally {
      setPlansLoading(false);
    }
  };

  // =========================
  // PASO 1: crear onboarding
  // =========================
  const handleStart = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!validateSchedule()) {
      setMessage("Horario inválido: el fin debe ser mayor al inicio.");
      return;
    }

    setLoading(true);
    try {
      const payload = {
        nombre: form.nombre.trim(),
        logo_url: (form.logo_url || "").trim(),
        direccion: form.direccion.trim(),
        horario_atencion: `${form.horario_inicio} - ${form.horario_fin}`,
        nombre_propietario: form.nombre_propietario.trim(),
        apellido_paterno_propietario: form.apellido_paterno_propietario.trim(),
        apellido_materno_propietario: form.apellido_materno_propietario.trim(),
        email: form.email.trim(),
        password: form.password,
      };

      const { response, data } = await startMicroempresaOnboarding(payload);

      if (!response.ok) {
        setMessage(data.error || "No se pudo iniciar el registro.");
        return;
      }

      // esperado: signup_id + tenant_id
      storage.set(KEY_SIGNUP_ID, data.signup_id || "");
      storage.set(KEY_TENANT_ID, data.tenant_id || "");
      storage.set(KEY_EMAIL, payload.email);

      setMessage(data.message || "Registro iniciado. Ahora selecciona un plan.");
      navigate("/registro/microempresa/plan");
    } finally {
      setLoading(false);
    }
  };

  // =========================
  // PASO 2: elegir plan
  // =========================
  const selectPlan = (plan) => {
    storage.set(KEY_PLAN_ID, plan.id_plan);
    storage.set(KEY_PLAN_NAME, plan.nombre);
    storage.set(KEY_PLAN_PRICE, plan.precio);
    setMessage("");
  };

  const continueToPayment = () => {
    if (!storage.get(KEY_PLAN_ID, "")) {
      setMessage("Selecciona un plan para continuar.");
      return;
    }
    navigate("/registro/microempresa/pago");
  };

  // =========================
  // PASO 3: subir comprobante
  // =========================
  const [file, setFile] = useState(null);

  const handleSubmitPayment = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!signupId) {
      setMessage("No hay registro iniciado (signup_id). Vuelve al paso 1.");
      return;
    }
    if (!selectedPlanId) {
      setMessage("Selecciona un plan (paso 2).");
      return;
    }
    if (!file) {
      setMessage("Sube un comprobante (PDF/JPG/PNG).");
      return;
    }

    setLoading(true);
    try {
      const { response, data } = await submitMicroempresaPayment({
        signup_id: signupId,
        id_plan: selectedPlanId,
        file,
      });

      if (!response.ok) {
        setMessage(data.error || "No se pudo enviar el comprobante.");
        return;
      }

      setMessage(data.message || "Enviado. Tu cuenta quedará en espera de validación.");
      navigate("/registro/microempresa/espera");
    } finally {
      setLoading(false);
    }
  };

  // =========================
  // PASO 4: espera
  // =========================
  const [statusLoading, setStatusLoading] = useState(false);
  const [status, setStatus] = useState(null);

  const refreshStatus = async () => {
    if (!signupId) return;
    setStatusLoading(true);
    setMessage("");
    try {
      const { response, data } = await getOnboardingStatus(signupId);
      if (!response.ok) {
        setMessage(data.error || "No se pudo consultar el estado.");
        return;
      }
      setStatus(data);
    } finally {
      setStatusLoading(false);
    }
  };

  useEffect(() => {
    // si entran directo al paso plan/pago, cargamos planes
    // el componente se monta 1 vez; el load se hace dentro del componente de route también,
    // pero aquí ayudamos si cambian.
  }, []);

  // =========================
  // UI: Steps via nested routes
  // =========================
  return (
    <Routes>
      <Route path="/" element={<Navigate to="datos" replace />} />

      {/* PASO 1 */}
      <Route
        path="datos"
        element={
          <div className="container">
            <SectionCard
              title="Registro Microempresa - Paso 1"
              description="Completa los datos básicos de la microempresa."
            >
              <form className="card" onSubmit={handleStart}>
                <label>
                  Nombre de microempresa
                  <input name="nombre" value={form.nombre} onChange={onChange} required />
                </label>

                <label>
                  Logo URL (opcional)
                  <input name="logo_url" type="url" value={form.logo_url} onChange={onChange} />
                </label>

                <label>
                  Dirección
                  <input name="direccion" value={form.direccion} onChange={onChange} required />
                </label>

                <label>
                  Horario inicio
                  <input
                    name="horario_inicio"
                    type="time"
                    value={form.horario_inicio}
                    onChange={onChange}
                    required
                  />
                </label>

                <label>
                  Horario fin
                  <input
                    name="horario_fin"
                    type="time"
                    value={form.horario_fin}
                    onChange={onChange}
                    required
                  />
                </label>

                <label>
                  Nombre del propietario
                  <input
                    name="nombre_propietario"
                    value={form.nombre_propietario}
                    onChange={onChange}
                    required
                  />
                </label>

                <label>
                  Apellido paterno del propietario
                  <input
                    name="apellido_paterno_propietario"
                    value={form.apellido_paterno_propietario}
                    onChange={onChange}
                    required
                  />
                </label>

                <label>
                  Apellido materno del propietario
                  <input
                    name="apellido_materno_propietario"
                    value={form.apellido_materno_propietario}
                    onChange={onChange}
                    required
                  />
                </label>

                <label>
                  Email
                  <input name="email" type="email" value={form.email} onChange={onChange} required />
                </label>

                <label>
                  Password
                  <input
                    name="password"
                    type="password"
                    value={form.password}
                    onChange={onChange}
                    required
                  />
                </label>

                <button type="submit" disabled={loading}>
                  {loading ? "Guardando..." : "Continuar (elegir plan)"}
                </button>

                {message && (
                  <p className={message.toLowerCase().includes("no") ? "error" : "muted"}>
                    {message}
                  </p>
                )}

                <div style={{ marginTop: 12 }}>
                  <Link className="link-button" to="/">
                    Volver al login
                  </Link>
                </div>
              </form>
            </SectionCard>
          </div>
        }
      />

      {/* PASO 2 */}
      <Route
        path="plan"
        element={
          <div className="container">
            <SectionCard title="Registro Microempresa - Paso 2" description="Selecciona el plan.">
              <div className="card">
                {!signupId && (
                  <p className="error">
                    No hay registro iniciado. Vuelve al paso 1.
                    <div style={{ marginTop: 10 }}>
                      <Link className="link-button" to="/registro/microempresa/datos">
                        Ir al paso 1
                      </Link>
                    </div>
                  </p>
                )}

                <div style={{ display: "flex", gap: 10, marginBottom: 10 }}>
                  <button
                    type="button"
                    className="ghost-button"
                    onClick={loadPlans}
                    disabled={plansLoading}
                  >
                    {plansLoading ? "Cargando..." : "Cargar planes"}
                  </button>
                  <Link className="link-button" to="/registro/microempresa/datos">
                    Volver
                  </Link>
                </div>

                {plans.length === 0 ? (
                  <p className="muted">Presiona “Cargar planes”.</p>
                ) : (
                  <div className="data-list">
                    {plans.map((p) => {
                      const active = String(p.id_plan) === String(selectedPlanId);
                      return (
                        <div className="data-row" key={p.id_plan}>
                          <div>
                            <div style={{ fontWeight: 600 }}>{p.nombre}</div>
                            <div className="muted">Precio: {p.precio}</div>
                            <div className="muted">Estado: {p.estado}</div>
                          </div>
                          <div className="row-actions">
                            <button
                              type="button"
                              className={active ? "ghost-button" : "link-button"}
                              onClick={() => selectPlan(p)}
                            >
                              {active ? "Seleccionado" : "Elegir"}
                            </button>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}

                <button
                  type="button"
                  style={{ marginTop: 12 }}
                  onClick={continueToPayment}
                  disabled={!signupId}
                >
                  Continuar (pago + comprobante)
                </button>

                {message && <p className="error">{message}</p>}
              </div>
            </SectionCard>
          </div>
        }
      />

      {/* PASO 3 */}
      <Route
        path="pago"
        element={
          <div className="container">
            <SectionCard
              title="Registro Microempresa - Paso 3"
              description="Escanea el QR y sube tu comprobante."
            >
              <div className="card">
                {!signupId || !selectedPlanId ? (
                  <p className="error">
                    Falta información. Vuelve al paso 1 y 2.
                    <div style={{ marginTop: 10, display: "flex", gap: 10 }}>
                      <Link className="link-button" to="/registro/microempresa/datos">
                        Paso 1
                      </Link>
                      <Link className="link-button" to="/registro/microempresa/plan">
                        Paso 2
                      </Link>
                    </div>
                  </p>
                ) : (
                  <>
                    <div className="muted" style={{ marginBottom: 10 }}>
                      Plan: <strong>{selectedPlanName}</strong> — Monto:{" "}
                      <strong>{selectedPlanPrice}</strong>
                    </div>

                    <div style={{ display: "flex", justifyContent: "center", marginBottom: 16 }}>
                      <div style={{ background: "white", padding: 12, borderRadius: 8 }}>
                        <QRCodeCanvas value={qrValue} size={220} />
                      </div>
                    </div>

                    <form onSubmit={handleSubmitPayment}>
                      <label>
                        Subir comprobante (PDF/JPG/PNG)
                        <input
                          type="file"
                          accept=".pdf,image/*"
                          onChange={(e) => setFile(e.target.files?.[0] || null)}
                        />
                      </label>

                      <button type="submit" disabled={loading}>
                        {loading ? "Enviando..." : "Enviar comprobante"}
                      </button>

                      {message && (
                        <p className={message.toLowerCase().includes("no") ? "error" : "muted"}>
                          {message}
                        </p>
                      )}
                    </form>

                    <div style={{ marginTop: 12, display: "flex", gap: 10 }}>
                      <Link className="link-button" to="/registro/microempresa/plan">
                        Volver a planes
                      </Link>
                    </div>
                  </>
                )}
              </div>
            </SectionCard>
          </div>
        }
      />

      {/* PASO 4 */}
      <Route
        path="espera"
        element={
          <div className="container">
            <SectionCard
              title="Cuenta en espera"
              description="Tu comprobante fue enviado. Un superusuario debe validar la cuenta."
            >
              <div className="card">
                {!signupId ? (
                  <p className="error">No hay registro iniciado (signup_id).</p>
                ) : (
                  <>
                    <p className="muted">
                      ID de registro: <strong>{signupId}</strong>
                    </p>

                    <button type="button" className="ghost-button" onClick={refreshStatus} disabled={statusLoading}>
                      {statusLoading ? "Consultando..." : "Consultar estado"}
                    </button>

                    {status && (
                      <div style={{ marginTop: 10 }}>
                        <div className="muted">
                          Estado: <strong>{status.estado || status.status || "pendiente"}</strong>
                        </div>
                        {status.message && <div className="muted">{status.message}</div>}
                      </div>
                    )}

                    {message && <p className="error">{message}</p>}

                    <div style={{ marginTop: 12 }}>
                      <Link className="link-button" to="/">
                        Ir al login
                      </Link>
                    </div>
                  </>
                )}
              </div>
            </SectionCard>
          </div>
        }
      />
    </Routes>
  );
}
