import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { requestPasswordReset } from "../controllers/authController";

const roleLabels = {
  super_usuario: "Super usuario",
  microempresa: "Microempresa",
  cliente: "Cliente",
};

export default function ForgotPasswordView() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [roleOptions, setRoleOptions] = useState([]);
  const [selectedRole, setSelectedRole] = useState(null);

  const sendRequest = async (role) => {
    setLoading(true);
    setMessage("");

    try {
      const { response, data } = await requestPasswordReset({
        email: email.trim(),
        role,
      });

      if (!response.ok) {
        setMessage(data.error || "Ocurrió un error");
        setRoleOptions([]);
        return;
      }

      if (data.select_role) {
        setRoleOptions(data.roles || []);
        setMessage("Este correo existe en más de un tipo de usuario. Selecciona tu rol.");
        return;
      }

      // ✅ Guardamos para que sobreviva recarga
      localStorage.setItem("pwreset_email", email.trim());
      localStorage.setItem("pwreset_role", role || "");

      setRoleOptions([]);
      setSelectedRole(role || null);

      // backend puede responder message
      setMessage(data.message || "Se envió el token a tu correo.");

      // pasamos a pantalla de reset
      navigate("/reset-password", { state: { email: email.trim(), role: role || null } });
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setRoleOptions([]);
    setSelectedRole(null);

    if (!email.trim()) {
      setMessage("Ingresa un correo válido.");
      return;
    }

    await sendRequest(undefined);
  };

  return (
    <div className="container">
      <h1>Recuperar contraseña</h1>

      <form className="card" onSubmit={handleSubmit}>
        <label>
          Ingresa tu correo
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="tu_correo@ejemplo.com"
            required
          />
        </label>

        <button type="submit" disabled={loading}>
          {loading ? "Enviando..." : "Enviar token"}
        </button>

        {message && <p className={message.toLowerCase().includes("error") ? "error" : "muted"}>{message}</p>}

        {roleOptions.length > 0 && (
          <div style={{ marginTop: 10 }}>
            <p className="muted">Selecciona el tipo de usuario:</p>
            <div className="role-options">
              {roleOptions.map((role) => (
                <button
                  key={role}
                  type="button"
                  disabled={loading}
                  onClick={() => sendRequest(role)}
                >
                  {roleLabels[role] || role}
                </button>
              ))}
            </div>
          </div>
        )}

        <div style={{ marginTop: 12 }}>
          <Link className="link-button" to="/">
            Volver al login
          </Link>
        </div>
      </form>
    </div>
  );
}
